"use strict";

const BaseService = require('./../service.js');
const config = require('./testConfig.json');
const testRoutes = require('./test-routes.js');

const service = new BaseService(config);

//Note how in the middleware function we are calling next(). THIS IS VERY IMPORTANT!
service
    .registerMiddleware((req, res, next) => {
        req.params.foo = 'bar';
        next();
    })
    .register(testRoutes)
    .start();

module.exports = {};